import { Component, effect, inject } from '@angular/core';
import { GetproductsService } from 'src/app/services/getproducts.service';
import { ProductType } from 'src/data/products';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})
export class TableComponent {
  data: ProductType[] = [];
  productService = inject(GetproductsService);
  EditComponent: boolean = false;
  disabled: boolean = false;

  enableEditing () {
    this.EditComponent = true;
  }

  getUpdatedData ( category: any, id: any, productname: any, quantity: any, serialno: any) {
    console.log({ category, id, productname, quantity, serialno });
  }

  editData(category: any, id: any, productname: any, quantity: any, serialno: any):any {
    this.EditComponent = true;
    this.productService.editData({id, category, productname, quantity, serialno});
    console.log("TABLE COMP UPDATED DATA: ", {id, category, productname, quantity, serialno})
  }

  onDisableData (id: number): any {
    this.productService.disableProduct(id);
    this.productService.getData();
  }

  constructor () {
    effect(() => {
      this.data = this.productService.getData() as any; 
    })
  }
}
