import { Injectable, signal, WritableSignal } from '@angular/core';
import { ProductType } from 'src/data/products';

@Injectable({
  providedIn: 'root'
})
export class GetproductsService {
  data: WritableSignal<ProductType[] | null> = signal([]);
  id: WritableSignal<number> = signal(1);

  setId () {
    return this.id.update(val => val+1);
  }

  putData(data: ProductType | any) {
    data = {...data, id: this.id()}
    this.setId();
    console.log(data);
    this.data.mutate((value) => value?.push(data))
  }

  disableProduct(id: number) {
    this.data.update((product: any) => product?.filter((a: any) => a.id !== id));
  }

  getData(): ProductType[] | null {
    return this.data();
  }

  editData({ category, id, productname, quantity }: ProductType) {
    console.log(this.data.update((value: any) => value?.find((a: any) => {
      if (a.id === id) {
        console.log("Found!")
      } else {
        console.log("Not FOund!")
      }
    })))
  }

  constructor() {}
}
