import { Component, Input, signal, WritableSignal } from '@angular/core';
import { TableComponent } from '../table/table.component';
import { CheckinComponent } from '../checkin/checkin.component';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  @Input() activeComponent: WritableSignal<typeof TableComponent | CheckinComponent> = signal(TableComponent);

  changeComponent(button: "table" | "checkin") {
    if (button == "table") {
      this.activeComponent.set(TableComponent);
    } else {
      this.activeComponent.set(CheckinComponent)
    }
  }
}
